function repeat(selector,json){var obj=$(selector),repeatObject={id:obj.attr("id"),data:json,context:obj.html(),exec:function(){for(obj.html(""),i=0;i<json.length;i++){var data=json[i],replace=this.context.match(/{{(.*?)}}/g),output=this.context;for(a=0;a<replace.length;a++){var value=eval(replace[a]);output=output.replace(replace[a],value)}obj.append(output)}$.each($("*[repeat-src]"),function(){$(this).attr("src",$(this).attr("repeat-src"))})}};repeatObject.exec()}
